    postsCount: lead?.postsCount || 0,
    profilePicUrl: lead?.profilePicUrlHD || lead?.profilePicUrl || '',
    externalUrl: lead?.externalUrl || '',
    businessCategory: lead?.businessCategoryName || '',
    isBusinessAccount: lead?.isBusinessAccount || false,
    isVerified: lead?.isVerified || false,
