const geminiResponse = $input.first().json;
const leadData = $('Prepara Dados').first().json;

let content = '';
if (geminiResponse.candidates && geminiResponse.candidates[0]?.content?.parts?.[0]?.text) {
  content = geminiResponse.candidates[0].content.parts[0].text;
}

let parsed = { tactics: [], messages: [], ideas: [], triggers: [] };
try {
  // Extract JSON from response
  const match = content.match(/\{[\s\S]*\}/);
  if (match) {
    parsed = JSON.parse(match[0]);
  }
} catch (e) {
  console.log('Parse error:', e.message);
  console.log('Content:', content);
}

// Map recent posts to carousel format
const recentPosts = (leadData.recentPosts || []).map((post, index) => {
  const isVideo = post.type === 'Video' || post.type === 'video';
  const caption = post.caption || '';
  const summary = caption.length > 100 ? caption.substring(0, 100) + '...' : caption;

  return {
    type: isVideo ? 'reel' : 'post',
    thumbnail: post.url || '',
    caption: caption,
    summary: summary,
    likes: post.likes || 0,
    comments: post.comments || 0,
    engagement: (post.likes || 0) + (post.comments || 0),
    connection_point: summary ? `Comentar sobre: "${summary.substring(0, 50)}..."` : 'Engajar com este conte√∫do',
    opportunity: isVideo ? 'Conte√∫do em v√≠deo - alta chance de engajamento' : 'Post visual - analisar est√©tica',
    theme: caption.substring(0, 30) || 'Conte√∫do do perfil',
    isVideo: isVideo
  };
});

return {
  json: {
    status: "success",
    profile: {
      username: leadData.username || '',
      full_name: leadData.fullName || '',
      profile_pic: leadData.profilePicUrl || '',
      bio: leadData.biography || '',
      following: leadData.followingCount || 0,
      followers: leadData.followersCount || 0,
      posts: leadData.postsCount || 0,
      website: leadData.externalUrl || '',
      category: leadData.businessCategory || ''
    },
    tactics: parsed.tactics || [],
    messages: parsed.messages || [],
    ideas: parsed.ideas || [],
    triggers: parsed.triggers || [],
    mentions_found: leadData.mentions || [],
    recent_posts: recentPosts,
    posts_analyzed: recentPosts.length
  }
};

